// Country data derived from ISO 3166 standard
// Flag emojis are generated from alpha2 codes using regional indicator symbols

export interface CountryData {
    name: string;
    alpha2: string;
    alpha3: string;
    numeric: string;
    phoneCode: string;
    continent: string;
    capital: string;
    timezone: string;
    currency: string;
    tld: string;
    areaKm2?: number;
    gdpUsd?: string;
    flag: string;
}

// Generate flag emoji from alpha2 code
const getFlag = (alpha2: string): string => {
    if (!alpha2 || alpha2.length !== 2) return '';
    const codePoints = alpha2
        .toUpperCase()
        .split('')
        .map(char => 127397 + char.charCodeAt(0));
    return String.fromCodePoint(...codePoints);
};

